import React, { useState } from 'react';
import axios from 'axios';

const ItemForm = ({ onItemCreated }) => {
  const [formData, setFormData] = useState({
    actualPrice: '',
    comment: '',
    eventVenuesID: '',
    imageUrl: '',
    listingPrice: '',
    ordersID: '',
    sellerID: '',
    sellingPrice: '',
    status: '',
    type: '',
    validatedOn: '',
    verified: true,
    lock: true
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('http://localhost:5000/cart/tickets', formData)
      .then(response => {
        alert('Item created successfully');
        onItemCreated(); // Fetch updated item list
        console.log(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Actual Price:</label>
        <input type="number" name="actualPrice" value={formData.actualPrice} onChange={handleChange} required />
      </div>
      <div>
        <label>Comment:</label>
        <input type="text" name="comment" value={formData.comment} onChange={handleChange} />
      </div>
      {/* Add similar input fields for other form fields */}
      <button type="submit">Create Item</button>
    </form>
  );
};

export default ItemForm;
