import React, { useState } from 'react';
import axios from 'axios';

const ItemForm = ({ onItemCreated }) => {
  const [formData, setFormData] = useState({
    actualPrice: '',
    comment: '',
    eventVenuesID: '',
    imageUrl: '',
    listingPrice: '',
    ordersID: '',
    sellerID: '',
    sellingPrice: '',
    status: '',
    type: '',
    validatedOn: '',
    verified: true,
    lock: true
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/cart/tickets', formData);
      alert('Item created successfully');
      console.log(response.data);
      onItemCreated(response.data.ticket);  // Call parent callback to refresh the list
      setFormData({
        actualPrice: '',
        comment: '',
        eventVenuesID: '',
        imageUrl: '',
        listingPrice: '',
        ordersID: '',
        sellerID: '',
        sellingPrice: '',
        status: '',
        type: '',
        validatedOn: '',
        verified: true,
        lock: true
      });
    } catch (error) {
      console.error('There was an error creating the item!', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {Object.keys(formData).map((key) => (
        <div key={key}>
          <label>{key.charAt(0).toUpperCase() + key.slice(1)}:</label>
          <input
            type={key === 'actualPrice' || key === 'listingPrice' || key === 'ordersID' || key === 'sellerID' || key === 'sellingPrice' || key === 'eventVenuesID' ? 'number' : 'text'}
            name={key}
            value={formData[key]}
            onChange={handleChange}
            required={key !== 'comment' && key !== 'imageUrl' && key !== 'validatedOn'}  // Make optional fields not required
          />
        </div>
      ))}
      <button type="submit">Create Item</button>
    </form>
  );
};

export default ItemForm;
